CREATE DATABASE retail;

USE retail;

IMPORT FROM  '/user/zeppelin/retail_export/categories';
IMPORT FROM  '/user/zeppelin/retail_export/customers';
IMPORT FROM  '/user/zeppelin/retail_export/departments';
IMPORT FROM  '/user/zeppelin/retail_export/order_items';
IMPORT FROM  '/user/zeppelin/retail_export/orders';
IMPORT FROM  '/user/zeppelin/retail_export/products';

%pyspark
number_of_order=spark.sql("select count(*) as number_of_order from retail.orders");
number_of_order.show()
number_of_saled_product=spark.sql("select count(*) as number_of_saled_product from retail.order_items");
number_of_saled_product.show()

%pyspark
retailTransactions=spark.sql("SELECT DISTINCT order_item_order_id,order_item_product_id FROM retail.order_items");
retailTransactions.show(10)
print("There are "+str(retailTransactions.count())+" transactions");

